import React, { useState, useEffect } from "react";
import ProjectCard from "./ProjectCard";

const ProjectSearch = ({ projects }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    // map transforms [].length => a new type of data at the same length
    // find reads an expression and then returns the first value that that expression is true
    // filter reads an expression and then returns an [] with values where expression was true
    // reduce
    const newResults = projects.filter((project) => {
      return (
        project.tag
          .toString()
          .toLowerCase()
          .includes(searchTerm.toLowerCase().trim().split(" ")) ||
        project.description
          .toLowerCase()
          .includes(searchTerm.toLowerCase().trim())
      );
    });

    setSearchResults(newResults);
  }, [searchTerm, projects]);

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="project-search">
      <div
        className={
          searchResults.length === 0 ? "searchBox search-error" : "searchBox"
        }
      >
        <input
          id="search"
          className="searchInput"
          type="text"
          name="search"
          placeholder="Search"
          value={searchTerm}
          onChange={handleChange}
        />

        <button className="searchButton">
          {/* <i className="material-icons">
                    search
                </i> */}
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="100"
            height="100"
            viewBox="0 0 100 100"
          >
            <path
              fill="currentColor"
              fill-rule="evenodd"
              d="M15.273 30.098c0-12.857 10.423-23.177 23.28-23.177 12.857 0 23.28 10.32 23.28 23.177S51.41 53.275 38.553 53.275c-12.857 0-23.28-10.32-23.28-23.177m-3.148-.215c0-14.446 11.654-26.099 26.1-26.099 14.445 0 26.099 11.653 26.099 26.099S52.67 55.982 38.225 55.982c-14.446 0-26.1-11.653-26.1-26.099z"
            />
            <path
              fill="currentColor"
              fill-rule="evenodd"
              d="M54.96 49.97c4.648-3.011 5.472 3.538 13.931 13.04C88.008 84.485 98.16 87.776 87.875 96.216c-10.31 8.461-13.74-5.514-26.71-28.201C54.851 56.971 50.13 53.098 54.96 49.97z"
            />
          </svg>
        </button>
      </div>
      <div className="project-grid">
        {searchResults.length === 0 ? (
          <div className="project-card">
            <div className="project-item">
              <h1>No Content found</h1>{" "}
              <p> Please try searching for something else!</p>
            </div>
          </div>
        ) : (
          searchResults.map((project) => {
            return <ProjectCard project={project} key={project.id} />;
          })
        )}
      </div>
    </div>
  );
};

export default ProjectSearch;
